<template>
    <div class="search">
        <figure style="--aspect-ratio: 1/1;">
            <g-link to="/">
                <img
                    class="page-logo"
                    src="data:image/svg+xml;base64,"
                    alt="Webiste logo, showing a strawberry heart in electric pink with green text"
                />
            </g-link>
        </figure>
        <div class="search-bar">
            <input type="text" v-model="query" class="search-input" placeholder="What are you looking for?">
        </div>
    </div>
</template>

<script>
import Magnify from 'vue-material-design-icons/Magnify.vue'
import { mapActions } from "vuex"
import debounce from 'lodash.debounce'

export default {
    name: 'SearchArea',
    components: { Magnify },
     data() {
        return {
          query: ''
        }
    },
    watch: {
        query: debounce(function (val) {
            this.filterOnProductName(val)
        }, 100)
    },
     methods: {
        ...mapActions(['filterOnProductName']),
    }
}
</script>

